package com.java.samples.util;

public class ReverseSample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x=156;
		int d;
		int rev=0;
		while(x!=0) {
		d=x%10;
		rev=rev*10+d;
		x=x/10;
	
		
			
	}
		String s1="abdul";
		String s2="azees";
		System.out.println(s1+s2);
		System.out.println("reverse="+rev);
	}

}
